<?phpsession_start();include_once('data/config.php');include_once('m/db.php');function __autoload($classname){    if (file_exists("c/$classname.php")){        include_once("c/$classname.php");    }    if (file_exists("m/$classname.php")){        include_once("m/$classname.php");    }    if (file_exists("data/$classname.php")){        include_once("data/$classname.php");    }}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']){	case 'user':		$controller = new C_User();		break;	case 'order':		$controller = new C_Order();		break;	case 'goods':		$controller = new C_Goods();		break;	case 'cart':		$controller = new C_Cart();		break;	default:		$controller = new C_Page();}$controller->Request($action);